                 <tr>\n                                                        <th colspan=\"2\" class=\"text-center\">                      \n                                                            <select  class=\"col-sm-12 input-sm\" id=\"opPdate\">\n                                                                <option value=\"\" >\u0e01\u0e23\u0e38\u0e13\u0e32\u0e40\u0e25\u0e37\u0e2d\u0e01<\/option>\n                                                                                                                                                                                                            <option value=\"2019-04-01\" >2019-04-01<\/option>\n                                                                                                                                            <option value=\"2019-03-16\" >2019-03-16<\/option>\n                                                                                                                                            <option value=\"2019-03-01\" >2019-03-01<\/option>\n                                                                                                                                            <option value=\"2019-02-16\" >2019-02-16<\/option>\n                                                                                                                                            <option value=\"2019-02-01\" >2019-02-01<\/option>\n                                                                                                                                            <option value=\"2019-01-17\" >2019-01-17<\/option>\n                                                                                                                                            <option value=\"2018-12-30\" >2018-12-30<\/option>\n                                                                                                                                            <option value=\"2018-12-16\" >2018-12-16<\/option>\n                                                                                                                                            <option value=\"2018-12-01\" >2018-12-01<\/option>\n                                                                                                                                            <option value=\"2018-11-16\" >2018-11-16<\/option>\n                                                                                                                                                                                                <\/select>\n                                                        <\/th>\n                                                        <th colspan=\"4\"  class=\"text-center\">\n                                                            \u0e41\u0e1e\u0e49\/\u0e0a\u0e19\u0e30 \n                                                            <span class=\"pull-right cur\" id=\"progress\">\n                                                                <i class=\"ace-icon fa fa-refresh\"><\/i>\n                                                            <\/span>\n                                                        <\/th>\n                                                    <\/tr>\n                                                    <tr class=\"text-center\">\n                                                        <th>\u0e2a\u0e21\u0e32\u0e0a\u0e34\u0e01<\/th>\n                                                        <th>\u0e22\u0e2d\u0e14\u0e0b\u0e37\u0e49\u0e2d<\/th>\n                                                        <th>\u0e22\u0e2d\u0e14\u0e40\u0e01\u0e47\u0e1a<\/th>\n                                                        <th>\u0e2a\u0e48\u0e27\u0e19\u0e25\u0e14<\/th>\n                                                        <th>\u0e23\u0e32\u0e07\u0e27\u0e31\u0e25<\/th>\n                                                        <th>\u0e22\u0e2d\u0e14\u0e2a\u0e38\u0e17\u0e18\u0e34<\/th>\n                                                    <\/tr>\n                                                <\/thead>\n                                                <tbody>\n                                                <\/tbody>\n                                                <tfoot><\/tfoot>\n                                            <\/table>\n                                        <\/div>\n                                    <\/div>\n                                <\/div>\n                            <\/div>\n                            <div class=\"col-sm-6\">\n                                <div class=\"widget-box\" id=\"reloadUserDetail\">\n                                    <div class=\"widget-header hidden\">\n                                        <h5 class=\"widget-title bigger lighter\">\n                                            <i class=\"ace-icon fa fa-user\"><\/i>\n                                            <span id=\"title_user\"><\/span>\n                                        <\/h5>\n                                        <div class=\"widget-toolbar\">\n                                            <a href=\"#\" data-action=\"reload\"  class=\"hidden\" >\n                                                <i class=\"ace-icon fa fa-refresh\"><\/i>\n                                            <\/a>\n                                        <\/div>\n                                    <\/div>\n                                    <div class=\"widget-body\">\n                                        <div class=\"widget-main no-padding\">\n                                            <div class=\"table-responsive\">\n                                                <table id=\"tb_lotteryDetail\" class=\"table table-striped table-bordered table-hover text-center\">\n                                                    <thead>\n                                                        <tr>\n                                                            <th>\u0e15\u0e31\u0e27\u0e40\u0e25\u0e02<\/th>\n                                                            <th>\u0e22\u0e2d\u0e14\u0e40\u0e01\u0e47\u0e1a<\/th>\n                                                            <th>\u0e2a\u0e48\u0e27\u0e19\u0e25\u0e14<\/th>\n                                                            <th>\u0e23\u0e32\u0e07\u0e27\u0e31\u0e25<\/th>\n                                                            <th>\u0e22\u0e2d\u0e14\u0e2a\u0e38\u0e17\u0e18\u0e34<\/th>\n                                                        <\/tr>\n                                                    <\/thead>\n                                                    <tbody>\n                                                    <\/tbody>\n                                                <\/table>\n                                            <\/div>\n                                        <\/div>\n                                    <\/div>\n                                <\/div>\n                            <\/div>\n                        <\/div>\n                    <\/form>\n                <\/div>\n            <\/div>\n        <\/div>\n    <\/div>\n<\/div>\n<script type=\"text\/javascript\">\n\n    $('#opPdate').on('change',function(e) {\n        getLotterySBU($('#opPdate').val());\n    });\n    $('#progress').on('click',function(e){\n        if($('#opPdate').val() != '' && $('#opPdate').val() != undefined){\n            getLotterySBU($('#opPdate').val());\n        }else{\n            $('#opPdate').css({\"box-shadow\":\"0 0 5px #DE5E47 \",\"margin\":\"5px 1px 3px 0px;\",\"border\":\"1px solid #DE5E47 \"}).click();\n        }\n    });\n    function getLotterySBU(pdate){\n        \n        $('#title_user').text('');\n        $('#tb_lotteryDetail tbody').html('');\n\n        if(pdate != '' && pdate != undefined){\n\n            $('#reloatLotterySBU').load('show');\n            $.ajax({\n                url: 'lotterySummaryByUserGet',\n                type: 'POST',\n                dataType: 'json',\n                data: { \n                    _token: $('input[name=_token]').val(),\n                    pdate : pdate\n                },\n            })\n            .done(function(response) {\n                var table = \"\";\n                var tfoot = \"\";\n                var sum   = {\n                    lottery_buy : 0,\n                    ukeep     : 0,\n                    pay_com   : 0,\n                    pay_reward : 0,\n                    stotal    : 0,\n                }\n                if(response.status){\n                    $.each(response.result,function(key,val){\n                        sum.lottery_buy   += Number(val.lottery_buy);\n                        sum.ukeep       += Number(val.ukeep);\n                        sum.pay_com     += Number(val.pay_com);\n                        sum.pay_reward  += Number(val.pay_reward);\n                        sum.stotal      += Number(val.stotal);\n\n                        table +='<tr id=\"'+ val.muser_id +'\" onclick=\"getUserDetail(&quot;'+ val.muser_id +'&quot;,&quot;'+ pdate +'&quot;);\" class=\"text-right cur\">'+\n                                    '<td>'+val.muser_id+'<\/td>'+\n                                    '<td class=\"num\">'+Number(val.lottery_buy).toFixed(2)+'<\/td>'+\n                                    '<td class=\"num\">'+Number(val.ukeep).toFixed(2)+'<\/td>'+\n                                    '<td class=\"num\">'+Number(val.pay_com).toFixed(2)+'<\/td>'+\n                                    '<td class=\"num\">'+Number(val.pay_reward).toFixed(2)+'<\/td>'+\n                                    '<td class=\"num\">'+Number(val.stotal).toFixed(2)+'<\/td>'+ \n                                '<\/tr>';\n                    });\n\n                    tfoot = '<tr class=\"text-right\">'+\n                                '<td><strong>\u0e23\u0e27\u0e21<\/strong><\/td>'+\n                                '<td class=\"fnum text-strong\">'+sum.lottery_buy.toFixed(2)+'<\/td>'+\n                                '<td class=\"fnum text-strong\">'+sum.ukeep.toFixed(2)+'<\/td>'+\n                                '<td class=\"fnum text-strong\">'+sum.pay_com.toFixed(2)+'<\/td>'+\n                                '<td class=\"fnum text-strong\">'+sum.pay_reward.toFixed(2)+'<\/td>'+\n                                '<td class=\"fnum text-strong\">'+sum.stotal.toFixed(2)+'<\/td>'+ \n                            '<\/tr>';\n                }else{\n                    table = '<tr><td colspan=\"11\" class=\"text-center text-danger\"> \u0e44\u0e21\u0e48\u0e1e\u0e1a\u0e02\u0e49\u0e2d\u0e21\u0e39\u0e25 <\/td><\/tr>';\n                }\n\n                $('#tb_lotterySBU tbody').html(table);\n                $('#tb_lotterySBU tfoot').html(tfoot);\n            }).always(function(){\n                $('td.num').digits().rmNoVal();\n                $('td.fnum').digits();\n                $('#reloatLotterySBU').load('hide');\n            });\n        }\n    }\n\n    function getUserDetail(user,date){\n        rmClass('tb_lotterySBU','info');\n        \n        $('#reloadUserDetail').load('show');\n        $('#'+user).addClass('info');\n        $.ajax({\n            url: 'lotterySummaryByUserDetail',\n            type: 'GET',\n            dataType: 'json',\n            data: { puser: user , pdate : date },\n        })\n        .done(function(response) {\n            var table = \"\";\n            if(response.status){\n                $('#title_user').text(user);\n\n                $.each(response.result,function(key,val){\n                    table +='<tr class=\"text-right\">'+\n                                '<td class=\"text-center\">'+val.lottery_number+'<\/td>'+\n                                '<td class=\"num\">'+Number(val.ukeep).toFixed(2)+'<\/td>'+\n                                '<td class=\"num\">'+Number(val.pay_com).toFixed(2)+'<\/td>'+\n                                '<td class=\"num\">'+Number(val.pay_reward).toFixed(2)+'<\/td>'+\n                                '<td class=\"num\">'+Number(val.stotal).toFixed(2)+'<\/td>'+ \n                            '<\/tr>';\n                });\n            }\n            $('#tb_lotteryDetail tbody').html(table);\n\n        }).always(function(){\n            $('td.num').digits().rmNoVal();\n            $('#reloadUserDetail').load('hide');\n        });\n    }\n\n<\/script>\n"}