                  <\/table>\n                                    <\/div>\n                                <\/div>\n                            <\/div>\n                        <\/div>\n                    <\/div>\n                <\/div>\n                            <\/div>\n        <\/div>\n    <\/div>\n<\/div>\n\n<script type=\"text\/javascript\">\n    $(document).ready(function(){\n        getUReturn(1,1);\n    });\n\n    function ckGames(id){\n        if($('#cka_'+id).is(':checked')) {\n            $('.ck_'+id).removeAttr('disabled');\n            $('.ck_'+id).removeClass('btn-light');\n        }else{\n            $('.ck_'+id).attr('disabled','disabled');\n            $('.ck_'+id).addClass('btn-light');\n        }\n    }\n\n\n    function ckEdit(id){\n        if($('#'+id).is(':checked')) {\n            $('.sl_'+id).removeAttr('disabled');\n        }else{\n            $('.sl_'+id).attr('disabled','disabled');\n        }\n    }\n\n    $('#btn_search').on('click',function(e){\n        getUReturn(1,1);\n    });\n    \n    function getUReturn(page,no){\n        $('#relodUReturn').load('show');\n        var table = \"\";\n\n        $.ajax({\n            url: 'memberUserReturn\/search.php',\n            type: 'POST',\n            dataType: 'json',\n            data: {\n                _token      : $('input[name=_token]').val(),\n                fuser       : $('input[name=fuser]').val(),\n                fuactive    : $('select[name=fuactive]').val(),\n                futype      : $('select[name=futype]').val(),\n                list_sort   : $('select[name=list_sort]').val(),\n                page        : page\n            },\n        })\n        .done(function(response) {\n            if(response.status){\n                if(Number(response.rows) >= 50){\n                    var pv   = page - 1;\n                    var nv   = no - 50;\n                    var pn   = page +1;\n                    var nn   = no + 50;\n\n                    if(nv < 1){\n                        $('#btn_prev').addClass('hidden');\n                    }else{\n                        $('#btn_prev').removeClass('hidden');\n                        $('#btn_next').removeClass('hidden');\n                    }\n                    $('#btn_prev').attr('onclick','getUReturn('+ pv +','+ nv +',\"prev\");');\n                    $('#btn_next').attr('onclick','getUReturn('+ pn +','+ nn +',\"next\");');\n\n                }else{\n                    var pv   = page - 1;\n                    var nv   = no - 50;\n                    $('#btn_prev').attr('onclick','getUReturn('+ pv +','+ nv +',\"prev\");');\n                    $('#btn_next').addClass('hidden');\n                }\n                $('#cka_get_return_step').removeAttr('disabled').attr(\"onchange\",\"ckGames('get_return_step')\");\n                $('#cka_get_return_sctoday').removeAttr('disabled').attr(\"onchange\",\"ckGames('get_return_sctoday')\");\n                $('#cka_get_return_sclive').removeAttr('disabled').attr(\"onchange\",\"ckGames('get_return_sclive')\");\n                $('#cka_get_return_sptoday').removeAttr('disabled').attr(\"onchange\",\"ckGames('get_return_sptoday')\");\n                $('#cka_get_return_splive').removeAttr('disabled').attr(\"onchange\",\"ckGames('get_return_splive')\");\n                var i = no;\n                $.each(response.result,function(key,val){\n                    var uname   = \"\";\n                    var game    = (val.get_return_game == \"Y\")? \"\u0e23\u0e31\u0e1a\" : \"\u0e44\u0e21\u0e48\u0e23\u0e31\u0e1a\";\n                    var casino  = (val.get_return_casino == \"Y\")? \"\u0e23\u0e31\u0e1a\" : \"\u0e44\u0e21\u0e48\u0e23\u0e31\u0e1a\";\n                    var party   = (val.get_return_party == \"Y\")? \"\u0e23\u0e31\u0e1a\" : \"\u0e44\u0e21\u0e48\u0e23\u0e31\u0e1a\";\n                    var vivo    = (val.get_return_vivo == \"Y\")? \"\u0e23\u0e31\u0e1a\" : \"\u0e44\u0e21\u0e48\u0e23\u0e31\u0e1a\";\n                    var allbet  = (val.get_return_allbet == \"Y\")? \"\u0e23\u0e31\u0e1a\" : \"\u0e44\u0e21\u0e48\u0e23\u0e31\u0e1a\";\n                    if(val.u_name != ''){\n                        uname = '('+ val.u_name +')';\n                    }\n                    if(response.show_only == 'N'){\n                        if(response.games_status.betgames != 'Y'){\n                            game = '<select class=\"form-control col-xs-12 col-sm-4 input-sm sl_'+ val.u_username +'\" id=\"'+ val.u_username +'_get_return_game\" name=\"get_return_game\" disabled=\"disabled\" onchange=\"setSl(\\'get_return_game\\',&quot;'+ val.u_username +'&quot;);\">'+ genSl(val.get_return_game,response.show_only) +'<\/select>';\n                            $('#cka_get_return_game').removeAttr('disabled').attr(\"onchange\",\"ckGames('get_return_game')\");\n                        }else{\n                            $('#cka_get_return_game').attr('disabled','disabled');\n                        }\n                        if(response.games_status.betcasinos != 'Y'){\n                            casino =  '<select class=\"form-control col-xs-12 col-sm-4 input-sm sl_'+ val.u_username +'\" id=\"'+ val.u_username +'_get_return_casino\" name=\"get_return_casino\" disabled=\"disabled\" onchange=\"setSl(\\'get_return_casino\\',&quot;'+ val.u_username +'&quot;);\">'+ genSl(val.get_return_casino,response.show_only) +'<\/select>';\n                            $('#cka_get_return_casino').removeAttr('disabled').attr(\"onchange\",\"ckGames('get_return_casino')\");\n                        }else{\n                            $('#cka_get_return_casino').attr('disabled','disabled');\n                        }\n                        if(response.games_status.betcasinos_p != 'Y'){\n                            party = '<select class=\"form-control col-xs-12 col-sm-4 input-sm sl_'+ val.u_username +'\" id=\"'+ val.u_username +'_get_return_party\" name=\"get_return_party\" disabled=\"disabled\" onchange=\"setSl(\\'get_return_party\\