l.u_username +'\" id=\"'+ val.u_username +'_get_return_tgp\" name=\"get_return_tgp\" disabled=\"disabled\" onchange=\"setSl(\\'get_return_tgp\\',&quot;'+ val.u_username +'&quot;);\">'+ genSl(val.get_return_tgp,response.show_only) +\n                                        '<\/select>';\n                            $('#cka_get_return_tgp').removeAttr('disabled').attr(\"onchange\",\"ckGames('get_return_tgp')\");\n                        }else{\n                            $('#cka_get_return_tgp').attr('disabled','disabled');\n                        }\n                        if(response.games_status.betcasinos_f != 'Y'){\n                            fg = '<select class=\"form-control col-xs-12 col-sm-4 input-sm sl_'+ val.u_username +'\" id=\"'+ val.u_username +'_get_return_fg\" name=\"get_return_fg\" disabled=\"disabled\" onchange=\"setSl(\\'get_return_fg\\',&quot;'+ val.u_username +'&quot;);\">'+ genSl(val.get_return_fg,response.show_only) +\n                                        '<\/select>';\n                            $('#cka_get_return_fg').removeAttr('disabled').attr(\"onchange\",\"ckGames('get_return_fg')\");\n                        }else{\n                            $('#cka_get_return_fg').attr('disabled','disabled');\n                        }\n                        if(response.games_status.betcasinos_r != 'Y'){\n                            rcb = '<select class=\"form-control col-xs-12 col-sm-4 input-sm sl_'+ val.u_username +'\" id=\"'+ val.u_username +'_get_return_rcb\" name=\"get_return_rcb\" disabled=\"disabled\" onchange=\"setSl(\\'get_return_rcb\\',&quot;'+ val.u_username +'&quot;);\">'+ genSl(val.get_return_rcb,response.show_only) +\n                                        '<\/select>';\n                            $('#cka_get_return_rcb').removeAttr('disabled').attr(\"onchange\",\"ckGames('get_return_rcb')\");\n                        }else{\n                            $('#cka_get_return_rcb').attr('disabled','disabled');\n                        }\n                        table += '<tr>'+\n                                    '<td>'+ i +'<\/td>'+\n                                    '<td>'+\n                                        '<div class=\"ace-settings-item\">'+\n                                            '<input type=\"checkbox\" class=\"ace ace-checkbox-2\" onchange=\"ckEdit(&quot;'+ val.u_username +'&quot;);\" id=\"'+ val.u_username +'\"\/>'+\n                                            '<label class=\"lbl\" for=\"ace-settings-navbar\"><\/label>'+\n                                        '<\/div>'+\n                                    '<\/td>'+\n                                    '<td class=\"text-left\">'+\n                                        '<strong> '+ val.u_username +' <\/strong>' + uname +\n                                    '<\/td>'+\n                                    '<td>'+\n                                        '<select class=\"form-control col-xs-12 col-sm-4 input-sm sl_'+ val.u_username +'\" id=\"'+ val.u_username +'_get_return_step\" name=\"get_return_step\" disabled=\"disabled\" onchange=\"setSl(\\'get_return_step\\',&quot;'+ val.u_username +'&quot;);\">'+ genSl(val.get_return_step,response.show_only) +\n                                        '<\/select>'+\n                                    '<\/td>'+\n                                    '<td>'+\n                                        '<select class=\"form-control col-xs-12 col-sm-4 input-sm sl_'+ val.u_username +'\" id=\"'+ val.u_username +'_get_return_sctoday\" name=\"get_return_sctoday\" disabled=\"disabled\" onchange=\"setSl(\\'get_return_sctoday\\',&quot;'+ val.u_username +'&quot;);\">'+ genSl(val.get_return_sctoday,response.show_only) +\n                                        '<\/select>'+\n                                    '<\/td>'+\n                                    '<td>'+\n                                        '<select class=\"form-control col-xs-12 col-sm-4 input-sm sl_'+ val.u_username +'\" id=\"'+ val.u_username +'_get_return_sclive\" name=\"get_return_sclive\" disabled=\"disabled\" onchange=\"setSl(\\'get_return_sclive\\',&quot;'+ val.u_username +'&quot;);\">'+ genSl(val.get_return_sclive,response.show_only) +\n                                        '<\/select>'+\n                                    '<\/td>'+\n                                    '<td>'+\n                                        '<select class=\"form-control col-xs-12 col-sm-4 input-sm sl_'+ val.u_username +'\" id=\"'+ val.u_username +'_get_return_sptoday\" name=\"get_return_sptoday\" disabled=\"disabled\" onchange=\"setSl(\\'get_return_sptoday\\',&quot;'+ val.u_username +'&quot;);\">'+ genSl(val.get_return_sptoday,response.show_only) +\n                                        '<\/select>'+\n                                    '<\/td>'+\n                                    '<td>'+\n                                        '<select class=\"form-control col-xs-12 col-sm-4 input-sm sl_'+ val.u_username +'\" id=\"'+ val.u_username +'_get_return_splive\" name=\"get_return_splive\" disabled=\"disabled\" onchange=\"setSl(\\'get_return_splive\\',&quot;'+ val.u_username +'&quot;);\">'+ genSl(val.get_return_splive,response.show_only) +\n                                        '<\/select>'+\n                                    '<\/td>'+\n                                    '<td>'+ game +'<\/td>'+\n                                    '<td>'+ casino +'<\/td>'+\n                                    '<td>'+ party +'<\/td>'+\n                                    '<td>'+ vivo +'<\/td>'+\n                                    '<td>'+ allbet +'<\/td>'+\n                                    '<td>'+ tgp +'<\/td>'+\n                                    '<td>'+ fg +'<\/td>'+\n                                    '<td>'+ rcb +'<\/td>'+\n                                '<\/tr>';\n                    }else{\n                        table += '<tr>'+\n                                    '<td>'+ i +'<\/td>'+\n                                    '<td><\/td>'+\n                                    '<td class=\"text-left\">'+\n                                        '<strong> '+ val.u_username +' <\/strong>' + uname +\n                                    '<\/td>'+\n                                    '<td>'+\n                                        genSl(val.get_return_step,response.show_only) +\n                                    '<\/td>'+\n                                    '<td>'+\n                                        genSl(val.get_return_sctoday,response.show_only) +\n                                    '<\/td>'+\n                                    '<td>'+\n                                        genSl(val.get_return_sclive,response.show_only) +\n                                    '<\/td>'+\n                                    '<td>'+\n                                        genSl(val.get_return_sptoday,response.show_only) +\n                                    '<\/td>'+\n                                    '<td>'+\n                                        genSl(val.get_return_splive,response.show_only) +\n                                    '<\/td>'+\n                                    '<td>'+ game +'<\/td>'+\n                                    '<td>'+ casino +'<\/td>'+\n                                    '<td>'+ party +'<\/td>'+\n                                    '<td>'+ vivo +'<\/td>'+\n                                    '<td>'+ allbet +'<\/td>'+\n                                    '<td>'+ tgp +'<\/td>'+\n                                    '<td>'+ fg +'<\/td>'+\n                                    '<td>'+ rcb +'<\/td>'+\n                                '<\/tr>';\n                    }\n\n                    i++;\n                });\n            }else{\n                table = '<tr>'+\n                            '<td colspan=\"13\" class=\"text-danger\"> \u0e44\u0e21\u0e48\u0e1e\u0e1a\u0e02\u0e49\u0e2d\u0e21\u0e39\u0e25 <\/td>'+\n                        '<\/tr>';\n                $('#cka_get_return_step').attr('disabled','disabled').removeAttr('onchange');\n                $('#cka_get_return_sctoday').attr('disabled','disabled').removeAttr('onchange');\n                $('#cka_get_return_sclive').attr('disabled','disabled').removeAttr('onchange');\n                $('#cka_get_return_sptoday').attr('disabled','disabled').removeAttr('onchange');\n                $('#cka_get_return_splive').attr('disabled','disabled').removeAttr('onchange');\n                $('#cka_get_return_game').attr('disabled','disabled').removeAttr('onchange');\n                $('#cka_get_return_casino').attr('disabled','disabled').removeAttr('onchange');\n                $('#cka_get_return_party').attr('disabled','disabled').removeAttr('onchange');\n                $('#cka_get_return_vivo').attr('disabled','disabled').removeAttr('onchange');\n                $('#cka_get_return_allbet').attr('disabled','disabled').removeAttr('onchange');\n                $('#cka_get_return_tgp').attr('disabled','disabled').removeAttr('onchange');\n            }\n        })\n        .always(function(response){\n            $('#tb_userreturn tbody').html(table);\n            $('#relodUReturn').load('hide');\n        });\n    }\n    function genSl(return_value,show_only){\n        var seY = \"\";\n        var seN = \"\";\n        var slOp  = \"\";\n        if(show_only == 'N'){\n            if(return_value == 'N'){\n                seN = \"selected\";\n            }else if(return_value == 'Y'){\n                seY = \"selected\";\n            }\n\n            slOp  = '<option value=\"N\" '+ seN +'> \u0e44\u0e21\u0e48\u0e23\u0e31\u0e1a <\/option>'+\n                    '<option value=\"Y\" '+ seY +'> \u0e23\u0e31\u0e1a <\/option>';\n        }else{\n            if(return_value == 'N'){\n                slOp = \"\u0e44\u0e21\u0e48\u0e23\u0e31\u0e1a\";\n            }else if(return_value == 'Y'){\n                slOp = \"\u0e23\u0e31\u0e1a\";\n            }\n        }\n\n\n        return slOp;\n    }\n    function setSl(game,user){\n        var title = game.split('_');\n        \/\/ var confirm = '<h5>Please confirm change get percent return ?<\/h5>';\n        var confirm = '<h5>\u0e01\u0e23\u0e38\u0e13\u0e32\u0e22\u0e37\u0e19\u0e22\u0e31\u0e19\u0e01\u0e32\u0e23\u0e23\u0e31\u0e1a\u0e04\u0e37\u0e19\u0e40\u0e1b\u0e2d\u0e23\u0e4c\u0e40\u0e0b\u0e47\u0e19\u0e15\u0e4c<\/h5>';\n        bootbox.dialog({\n            size : '',\n            message : confirm,\n            buttons:            \n            {\n                \"success\" :\n                 {\n                    \"label\" : \"\u0e1a\u0e31\u0e19\u0e17\u0e36\u0e01\",\n                    \"className\" : \"btn-sm btn-success\",\n                    \"callback\": function() {\n                        $.ajax({\n                            url: 'memberUserReturn\/save.php',\n                            type: 'POST',\n                            dataType: 'json',\n                            data: {\n                                _token   : $('input[name=_token]').val(),\n                                username : user,\n                                ctype    : game,\n                                cvalue   : $('#'+user+'_'+game).val(),\n                            },\n                        })\n                        .done(function(response) {\n                            if(response.status){\n                                \/\/ alert(response.msg);\n                                dialogSuccess(response.msg);\n                            }\n                        });\n                    }\n                },\n                \"button\" :\n                {\n                    \"label\" : \"\u0e22\u0e01\u0e40\u0e25\u0e34\u0e01\",\n                    \"className\" : \"btn-sm\"\n                }\n            }\n        });\n    }\n\n    function setAllSl(game,value){\n\n        var confirm = '';\n        if(value == 'N'){\n            \/\/ confirm = '<h5>Please confirm change not get precent return all ?<\/h5>';\n            confirm = '<h5>\u0e01\u0e23\u0e38\u0e13\u0e32\u0e22\u0e37\u0e19\u0e22\u0e31\u0e19\u0e01\u0e32\u0e23\u0e44\u0e21\u0e48\u0e23\u0e31\u0e1a\u0e04\u0e37\u0e19\u0e40\u0e1b\u0e2d\u0e23\u0e4c\u0e40\u0e0b\u0e47\u0e19\u0e15\u0e4c\u0e17\u0e31\u0e49\u0e07\u0e2b\u0e21\u0e14<\/h5>';\n        }else if(value == 'Y'){\n            \/\/ confirm = '<h5>Please confirm change get precent return all  ?<\/h5>';\n            confirm = '<h5>\u0e01\u0e23\u0e38\u0e13\u0e32\u0e22\u0e37\u0e19\u0e22\u0e31\u0e19\u0e01\u0e32\u0e23\u0e23\u0e31\u0e1a\u0e04\u0e37\u0e19\u0e40\u0e1b\u0e2d\u0e23\u0e4c\u0e40\u0e0b\u0e47\u0e19\u0e15\u0e4c\u0e17\u0e31\u0e49\u0e07\u0e2b\u0e21\u0e14<\/h5>';\n        }\n        bootbox.dialog({\n            size : '',\n            message : confirm,\n            buttons:            \n            {\n                \"success\" :\n                 {\n                    \"label\" : \"\u0e1a\u0e31\u0e19\u0e17\u0e36\u0e01\",\n                    \"className\" : \"btn-sm btn-success\",\n                    \"callback\": function() {\n                        $.ajax({\n                            url: 'memberUserReturn\/save.php',\n                            type: 'POST',\n                            dataType: 'json',\n                            data: {\n                                _token   : $('input[name=_token]').val(),\n                                username : '',\n                                utype    : $('select[name=futype]').val(),\n                                ctype    : game,\n                                cvalue   : value,\n                            },\n                        })\n                        .done(function(response) {\n                            if(response.status){\n                                \/\/ alert(response.msg);\n                                dialogSuccess(response.msg);\n                                getUReturn(1,1);\n                            }\n                        });\n                    }\n                },\n                \"button\" :\n                {\n                    \"label\" : \"\u0e22\u0e01\u0e40\u0e25\u0e34\u0e01\",\n                    \"className\" : \"btn-sm\"\n                }\n            }\n        });\n    }\n<\/script>\n"}